mapper.select:
select 

from USR_WESTRECIPE_REFUND
where STOCK_ID = #{stockId,jdbcType=VARCHAR}
  and WESTREFUND_ID = #{westrefundId,jdbcType=VARCHAR}

mapper.delete:
delete from USR_WESTRECIPE_REFUND
where STOCK_ID = #{stockId,jdbcType=VARCHAR}
  and WESTREFUND_ID = #{westrefundId,jdbcType=VARCHAR}
